import React from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import App from './App';

class Core extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showComponent: false,
      body: "",
      error: null,
      isLoaded: false,
      items: [],
      itemName: "",
      numItems: null,
      coins: []
    };
    this.mySubmitHandler = this.mySubmitHandler.bind(this);
    this.myChangeHandler = this.myChangeHandler.bind(this);
  }

  mySubmitHandler = async (event) => {
    event.preventDefault();
    axios({
      method: 'post',
      url: 'https://www.binance.com/bapi/nft/v1/public/nft/product-list',
      headers: {
        "accept": "*/*",
        "accept-language": "it-IT,it;q=0.9,en-US;q=0.8,en;q=0.7",
        "clienttype": "web",
        "content-type": "application/json",
        "lang": "en"
      },
      data: this.state.body
    })
      .then((response) => {
        this.setState({
          isLoaded: true,
          showComponent: true,
          items: response.data.data.rows,
          numItems: response.data.data.total
        });
      })
      .catch((error) => 
      {
        this.setState({
          isLoaded: true,
          showComponent: true,
          error
        });
      });
      axios({
        method: 'get',
        url: 'https://api.coingecko.com/api/v3/simple/price?ids=binancecoin%2Cethereum&vs_currencies=usd'
      })
        .then((response) => {
          this.setState({
            coins: response
          });
        })
        .catch((error) => 
        {
          this.setState({
            error
          });
        });

        console.log(this.state.items)
  }
  myChangeHandler = (event) => {
    this.setState({
      body: "{\"category\":0,\"keyword\":\"" + event.target.value + "\",\"orderBy\":\"list_time\",\"orderType\":-1,\"page\":1,\"rows\":100}",
      itemName: event.target.value
    });
  }
  render() {

    return (
      <div>
      <form onSubmit={this.mySubmitHandler}>
        <p>Enter Box name, and submit:</p>

        <TextField
          onChange={this.myChangeHandler}
          id="standard-basic"
          label="Search mystery box"
          style={{ margin: 10 }} />
        <Button
          style={{ margin: 12 }}
          variant="contained"
          color="primary"
          onChange={this.myChangeHandler}
          type='submit'
        >
          Search
        </Button>
      </form>

      {this.state.showComponent ?
      <App data={this.state}/> :
      null
     }
     </div>
    );
  }
}

export { Core };
