import React from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import App from './App';

class Core extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showComponent: false,
      body: "",
      page: 1,
      error: null,
      isLoaded: false,
      coinsLoad : false,
      items: [],
      itemName: "",
      numItems: null,
      coins: []
    };
    this.mySubmitHandler = this.mySubmitHandler.bind(this);
    this.myChangeHandler = this.myChangeHandler.bind(this);
  }

  async loadCoins() {
    await axios({
      method: 'get',
      url: 'https://api.coingecko.com/api/v3/simple/price?ids=ethereum%2Cbinancecoin&vs_currencies=usd'
    })
      .then((response) => {
        this.setState({
          coins: response,
          coinsLoad: true
        });
      })
      .catch((error) => 
      {
        this.setState({
          error
        });
      });
  }

  async componentDidMount(numItems, page, body) {
    if (numItems > 100) {
      this.setState({
        numItems: numItems - 100
      })
      await axios({
        method: 'post',
        url: 'https://www.binance.com/bapi/nft/v1/public/nft/product-list',
        headers: {
          "accept": "**",
          "accept-language": "it-IT,it;q=0.9,en-US;q=0.8,en;q=0.7",
          "clienttype": "web",
          "content-type": "application/json",
          "lang": "en"
        },
        data: this.state.body
      })
      .then((response) => {
        this.setState.Push( prevState => ({
          items: [...prevState.arrayvar, response.data.data.rows],
        }));
        if (this.numItems > 100) {
          this.setState({
              page: this.state.page++,
              body: "{\"category\":0,\"keyword\":\"" + this.state.itemName + "\",\"orderBy\":\"list_time\",\"orderType\":-1,\"page\":" + this.state.page + ",\"rows\":100}",
          });
          this.componentDidMount(this.state.numItems, this.state.page, this.state.body)
        }
      })
      .catch((error) => 
      {
        this.setState({
          error
        });
      });
    }
  }

  mySubmitHandler = async (event) => {
    event.preventDefault();
    await axios({
      method: 'post',
      url: 'https://www.binance.com/bapi/nft/v1/public/nft/product-list',
      headers: {
        "accept": "*/*",
        "accept-language": "it-IT,it;q=0.9,en-US;q=0.8,en;q=0.7",
        "clienttype": "web",
        "content-type": "application/json",
        "lang": "en"
      },
      data: this.state.body
    })
      .then((response) => {
        this.setState({
          isLoaded: true,
          showComponent: true,
          items: response.data.data.rows,
          numItems: response.data.data.total
        });
        this.loadCoins()
        /*if (this.numItems > 100) {
          this.setState({
              page: this.state.page++,
              body: "{\"category\":0,\"keyword\":\"" + this.state.itemName + "\",\"orderBy\":\"list_time\",\"orderType\":-1,\"page\":" + this.state.page + ",\"rows\":100}",
          });
          this.componentDidMount(this.state.numItems, this.state.page, this.state.body)
        }*/
      })
      .catch((error) => 
      {
        this.setState({
          isLoaded: true,
          showComponent: true,
          error
        });
      });
  }
  myChangeHandler = (event) => {
    this.setState({
      itemName: event.target.value,
      body: "{\"category\":0,\"keyword\":\"" + this.state.itemName + "\",\"orderBy\":\"list_time\",\"orderType\":-1,\"page\":" + this.state.page + ",\"rows\":100}",
    });
  }
  render() {

    return (
      <div>
      <form onSubmit={this.mySubmitHandler}>
        <p>Enter Box name, and submit:</p>

        <TextField
          onChange={this.myChangeHandler}
          id="standard-basic"
          label="Search mystery box"
          style={{ margin: 10 }} />
        <Button
          style={{ margin: 12 }}
          variant="contained"
          color="primary"
          onChange={this.myChangeHandler}
          type='submit'
        >
          Search
        </Button>
      </form>

      {this.state.showComponent ?
      <App data={this.state}/> :
      null
     }
     </div>
    );
  }
}

export { Core };
